import './Board.css';
import Cell from './Cell.js'

function Board({rows, columns}) {
  const mines = rows === 9 ? 10 : rows === 16 ? 40 : 99;
  console.log("from box: ", rows, columns, mines)
  return (
    <div className="board_wrapper">

        <Cell mines={mines}/>
    </div>
  );
}

export default Board;
