import './Game.css';
import Board from './Board.js'

function Game({ selectedLevel }) {
  const rows = selectedLevel === "beginner" ? 9 : selectedLevel === "intermediate" ? 16 :  24;
  const columns = selectedLevel === "beginner" ? 9 : selectedLevel === "intermediate" ? 16 :  24;
  console.log("Hello ", selectedLevel, "\nrows: ", rows, "\n columns: ", columns)
  return (
    <div className="game_wrapper">
      <p>Grid Size: {rows} x {rows}</p>
        <Board rows={rows} columns={columns}/>
    </div>
  );
}

export default Game;
