import './Board.css';
import { useState, useEffect } from 'react';
import Cell from './Cell.js'

// TODO: here, I will decide number of mines and number of cells required

function Board({ rows, selectedLevel }) {
  const mines = rows === 9 ? 10 : rows === 16 ? 40 : 99;
  console.log("from box: ", rows, mines)
  const [timer, setTimer] = useState(0);

  useEffect(() => {
    let interval;

    if (selectedLevel !== null) {
      setTimer(0);
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer + 1);
      }, 1000);
    }

    return () => {
      clearInterval(interval);
    };
  }, [selectedLevel]);

  const matrix = Array.from({ length: rows }).map((_, rowIndex) => (
    <div key={rowIndex} className="row">
      {Array.from({ length: rows }).map((_, colIndex) => (
        <Cell key={colIndex} mines={mines}/>
      ))}
    </div>
  ));

  return (
    <div className="board_wrapper">
      <div className='board_data'>
        <div>Grid: {rows} x {rows}</div>
        <div> Time: {timer}s</div>
        <div>flags: {mines}</div>
      </div>
      <div className={`grid grid-${rows}`}>
        {matrix}
      </div>
    </div>
  );
}

export default Board;
