import './Board.css';
import { useState, useEffect } from 'react';
import Cell from './Cell.js'

// TODO: here, I will decide number of mines and number of cells required

function Board({ rows, selectedLevel, mines }) {
  const [timer, setTimer] = useState(0);
  useEffect((rows, mine)=> {
    const grid = Array.from({ length: rows }, () =>
      Array.from({ length: rows }, () => (Math.random() < 0.3 ? "ðŸ’£" : ""))
    );
  }, []);


  useEffect(() => {
    let interval;
    if (selectedLevel !== null) {
      setTimer(0);
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer + 1);
      }, 1000);
    }
    return () => {
      clearInterval(interval);
    };
  }, [selectedLevel]);

  return (
    <div className="board_wrapper">
      <div className='board_data'>
        <div>
          <p>Grid: {rows} x {rows}</p>
        </div>
        <div>
          <p>Time: {timer}s</p>
        </div>
        <div>
          <p>flags: {mines}</p>
        </div>
      </div>
      <div className={`grid grid-${rows}`}>
      {grid.flat().map((value, index) => (
        <Cell key={index} value={value} />
      ))}
      </div>
    </div>
  );
}

export default Board;
