import { useState, useEffect } from 'react';
import Cell from './Cell.js'

// TODO: here, I will decide number of mines and number of cells required

function Board({ rows, selectedLevel, mines }) {
  const [timer, setTimer] = useState(0);
  const initialGrid = Array(rows).fill(null).map(() => Array(rows).fill(''));
  const [grid, setGrid] = useState(initialGrid);
  useEffect((initialGrid, rows, mines)=> {
