import './Board.css';
import { useState, useEffect } from 'react';
import Cell from './Cell.js'

// TODO: here, I will decide number of mines and number of cells required

function Board({ rows, selectedLevel }) {
  const mines = rows === 9 ? 10 : rows === 16 ? 40 : 99;
  console.log("from box: ", rows, mines)
  const [timer, setTimer] = useState(0); // Timer state
  // const [isGameActive, setIsGameActive] = useState(false);

  useEffect(() => {
    let interval;

    if (selectedLevel !== null) {
      setIsGameActive(true);
      setTimer(0);

      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer + 1);
      }, 1000);
    }

    return () => {
      clearInterval(interval);
      setIsGameActive(false);
    };
  }, [selectedLevel]);

  const matrix = Array.from({ length: rows }).map((_, rowIndex) => (
    <div key={rowIndex} className="row">
      {Array.from({ length: rows }).map((_, colIndex) => (
        <Cell key={colIndex} />
      ))}
    </div>
  ));

  return (
    <div className="board_wrapper">
      <div className='game'>
        <p>Grid Size: {rows} x {rows}</p>
        <p> Time: {timer}s</p>
        <p>flags: {mines}</p>
      </div>
      <div className={`grid grid-${rows}`}>
        {matrix}
      </div>
    </div>
  );
}

export default Board;
