import './Game.css';
import Board from './Board.js'

// TODO: here I will decide, winning and number of rows

function checkWinCondition(grid, mines) {
  let revealedCount = 0;
  let correctFlags = 0;

  grid.forEach(row => {
    row.forEach(cell => {
      if (cell.isRevealed) revealedCount++;
      if (cell.isFlagged && cell.isBomb) correctFlags++;
    });
  });

  return revealedCount + mines === grid.length * grid.length || correctFlags === mines;
}


function Game({ selectedLevel }) {
  const rows = selectedLevel === "beginner" ? 9 : selectedLevel === "intermediate" ? 16 :  24;
  const mines = rows === 9 ? 10 : rows === 16 ? 40 : 99;
  return (
    <div className="game_wrapper">
      <Board rows={rows} selectedLevel={selectedLevel} mines={mines}/>
    </div>
  );
}

export default Game;
