import './Cell.css';
import React from 'react';

const Cell = ({ value, onClick, isRevealed }) => {
  function handleRightClick(e, row, col) {
  e.preventDefault();
  const newGrid = grid.map(row => row.map(cell => ({ ...cell })));
  newGrid[row][col].isFlagged = !newGrid[row][col].isFlagged;
  setGrid(newGrid);
}

  return (
    <div className='cell_wrapper'>
      <button
        className={isRevealed ? 'revealed' : ''}
        onClick={onClick}
        disabled={isRevealed}
      >
        {isRevealed ? value : ""}
      </button>
    </div>
  );
};

export default Cell;
