import './Board.css';
import { useState, useEffect } from 'react';
import Cell from './Cell.js';

function Board({ rows, selectedLevel, mines }) {
  const [timer, setTimer] = useState(0);
  const [grid, setGrid] = useState([]);

  // Create rows x rows empty grid using Cell component
  function CreateGrid(rows) {
    const emptyGrid = Array(rows)
      .fill(null)
      .map(() => Array(rows).fill({ value: 0, isBomb: false, isRevealed: false }));
    return emptyGrid;
  }

  // Function to randomly place bombs


  // Initialize the grid with mines when the component mounts or rows/mines change
  useEffect((grid, ) => {
    function PlaceBomb(grid, mines) {
      const newGrid = [...grid];
      let placedMines = 0;

      while (placedMines < mines) {
        const row = Math.floor(Math.random() * rows);
        const col = Math.floor(Math.random() * rows);

        // Place a bomb only if the cell doesn't already have one
        if (!newGrid[row][col].isBomb) {
          newGrid[row][col] = { ...newGrid[row][col], isBomb: true };
          placedMines++;
        }
      }

      return newGrid;
    }
    if (rows > 0 && mines > 0) {
      const emptyGrid = CreateGrid(rows);
      const gridWithBombs = PlaceBomb(emptyGrid, mines);
      setGrid(gridWithBombs);
    }
  }, [rows, mines]);

  useEffect(() => {
    let interval;
    if (selectedLevel !== null) {
      setTimer(0);
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer + 1);
      }, 1000);
    }
    return () => {
      clearInterval(interval);
    };
  }, [selectedLevel]);

  return (
    <div className="board_wrapper">
      <div className="board_data">
        <div>
          <p>Grid: {rows} x {rows}</p>
        </div>
        <div>
          <p>Time: {timer}s</p>
        </div>
        <div>
          <p>Flags: {mines}</p>
        </div>
      </div>
      <div className={`grid grid-${rows}`}>
        {grid.map((row, rowIndex) =>
          row.map((cell, colIndex) => (
            <Cell key={`${rowIndex}-${colIndex}`} value={cell.isBomb ? "ðŸ’£" : cell.value} />
          ))
        )}
      </div>
    </div>
  );
}

export default Board;
