import './Cell.css';
import { useState, useEffect } from 'react';

// TODO: Here I will, I will travel the cell and Count nearby bomb and check whether the cell is open or not

function Cell({rows, mines}) {
  const [buttonText, setButtonText] = useState(null);

  // const handleClick = () => {
  //   setButtonText('1');
  // };

  const initialGrid = Array.from({ length: rows }).map((_, rowIndex) => (
    <div key={rowIndex} className="row">
      {Array.from({ length: rows }).map((_, colIndex) => (
        <Cell key={colIndex} mines={mines} />
      ))}
    </div>
  ));
  const randomizeGrid = () => {
    const gridCopy = initialGrid.map(row => row.slice());  // Create a copy of the grid
    const numXs = Math.floor(Math.random() * (81)); // Randomly decide how many Xs (up to 81)

    for (let i = 0; i < numXs; i++) {
      const row = Math.floor(Math.random() * 9); // Random row index
      const col = Math.floor(Math.random() * 9); // Random column index
      gridCopy[row][col] = 'X';
    }

    return gridCopy;
  };

  useEffect(() => {
    setButtonText(randomizeGrid());
  }, []);

  return (
    <div className="cell_wrapper">
      {/* <button onClick={randomizeGrid}>
        {buttonText}
      </button> */}


    </div>
  );
}

export default Cell;
