import './App.css';
import { useState } from 'react';
// import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Game from './Game.js';

function App() {
  const [selectedLevel, setSelectedLevel] = useState(null);

  const handleLevelChange = (event) => {
    setSelectedLevel(event.target.value);
  };

  return (
    <div className="home_wrapper">
      {selectedLevel === null ? (
        <div className="level_wrapper">
          <div className="header_wrapper">
            <h1>ðŸ’£ Minesweeper ðŸš©</h1>
          </div>
          <h2>Choose the level:</h2>
          <select name="level" defaultValue="---Select a Level---" onChange={handleLevelChange}>
            <option value="---Select a Level---" disabled>---Select a Level---</option>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
        </div>
      ) : (
        <div className="level_wrapperheader_wrapper game_container">
          <Game selectedLevel={selectedLevel} />
        </div>
      )}
      {/* <Router>
        <Routes>
          <Route path="/" element={<App />} />
          <Route path="/game" element={<Game />} />
        </Routes>
      </Router> */}
    </div>
  );
}

export default App;
