  //       <Cell key={colIndex} mines={mines} />
  //     ))}
  //   </div>
  // ));
  const initialGrid = Array(rows).fill(null).map(() => Array(rows).fill(''));


  const [grid, setGrid] = useState(initialGrid);
  useEffect((inital) => {
    const randomizeGrid = () => {
      const gridCopy = initialGrid.map(row => row.slice());  // Create a copy of the grid
      const numXs = Math.floor(Math.random() * mines); // Randomly decide how many Xs (up to 81)

      for (let i = 0; i < numXs; i++) {
        const row = Math.floor(Math.random() * rows); // Random row index
        const col = Math.floor(Math.random() * rows); // Random column index
        gridCopy[row][col] = 'ðŸ’£';
      }

      return gridCopy;
    };
    setGrid(randomizeGrid());
  }, []);

  return (
    <div className="cell_wrapper">
      {/* <button onClick={randomizeGrid}>
        {buttonText}
      </button> */}

<div>
      <h1>Random X Grid</h1>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(9, 40px)', gap: '5px' }}>
        {grid.map((row, rowIndex) =>
          row.map((cell, colIndex) => (
            <div
              key={`${rowIndex}-${colIndex}`}
              style={{
                width: '40px',
                height: '40px',
                textAlign: 'center',
                lineHeight: '40px',
                border: '1px solid #ccc',
                backgroundColor: cell === 'X' ? 'lightblue' : 'white',
                fontWeight: 'bold',
              }}
            >
              {cell}
            </div>
          ))
        )}
      </div>
    </div>


    </div>
  );
}

export default Cell;
