import './Board.css';
import Cell from './Cell.js'

// TODO: here, I will decide number of mines and number of cells

function Board({rows}) {
  const mines = rows === 9 ? 10 : rows === 16 ? 40 : 99;
  console.log("from box: ", rows, mines)

  const cells = Array.from({ length: rows }).map((_, index) => (
    <Cell key={index} />
  ));
  return (
    <div className="board_wrapper">
        {cells}
    </div>
  );
}

export default Board;
